import{_ as i,m as c,x as r,o as n,c as v,d as f}from"./index-bb066d33.js";const p="/MyPortfolio/assets/TravelMaker_Banner-7430852c.png",m="/MyPortfolio/assets/TravelMaker_Intro-d8fb542b.png",g="/MyPortfolio/assets/TravelMaker_Plan-d65319fc.png",b="/MyPortfolio/assets/TravelMaker_RIP-b0abb2ed.png",A="/MyPortfolio/assets/TravelMaker_SurveyResult-995de646.png",u="/MyPortfolio/assets/TravelMaker_Insight-a9bdff22.png",y="/MyPortfolio/assets/TravelMaker_Insight2-cafa1eed.png",x="/MyPortfolio/assets/TravelMaker_Reschedule-2b6cb04d.png",M="/MyPortfolio/assets/TravelMaker_Persona-553546a2.png",h="/MyPortfolio/assets/TravelMaker_CompetitiveAnalysis-775563ae.png",w="/MyPortfolio/assets/TravelMaker_Reschedule2-7cb4af8e.png",B="/MyPortfolio/assets/TravelMaker_Reschedule3-cd5024db.png",k="/MyPortfolio/assets/TravelMaker_TA-95395ec4.png",T="/MyPortfolio/assets/TravelMaker_HeroPage-1794a6aa.png",I="data:image/png;base64,",P="/MyPortfolio/assets/TravelMaker_IA-170a7884.png",Y="/MyPortfolio/assets/TravelMaker_Sketch-0611eb33.png",C="/MyPortfolio/assets/TravelMaker_Wireflow-47ea7183.png",R="/MyPortfolio/assets/TravelMaker_Prototype-1691cd41.png",E="data:image/png;base64,",j="/MyPortfolio/assets/TravelMaker_ABTest_A-6704b59a.png",S="/MyPortfolio/assets/TravelMaker_ABTest_B-5b6e6e65.png",Q="data:image/png;base64,",H="/MyPortfolio/assets/TravelMaker_Styleguide-71e16778.png",X="data:image/png;base64,",O="/MyPortfolio/assets/TravelMaker_UsabilityTest-bc219839.png",q="/MyPortfolio/assets/TravelMaker_Nextsteps-125b26d6.png",D="/MyPortfolio/assets/Travelmaker_Logo-810e2de1.svg";const G={class:"container-fluid"},U=f('<div class="row" data-bs-spy="scroll" data-bs-target="#sideNav" data-bs-smooth-scroll="true" tabindex="0" data-v-755554fe><div class="col-12 banner p-0" id="item-1" data-v-755554fe><img src="'+p+'" class="img-fluid" alt="..." data-v-755554fe></div><div class="col-3 d-none d-lg-block" data-v-755554fe></div><div class="col-12 col-lg-6 content" data-v-755554fe><div class="pt-5 border-start" data-v-755554fe><div class="pt-5 pb-5 row justify-content-center" data-v-755554fe><div class="col-12 col-md-8" data-v-755554fe><img src="'+m+'" class="img-fluid" alt="" data-v-755554fe></div></div><div id="item-2" class="mt-5 mb-5" data-v-755554fe><h2 class="text-center display-5 mt-5 mb-3 fw-bold" data-v-755554fe>規劃旅遊行程</h2><img src="'+g+'" class="img-fluid" alt="" data-v-755554fe><div class="row justify-content-center" data-v-755554fe><div class="col-12 col-md-10 mt-4 mb-4" data-v-755554fe><h5 class="text-center m-0" data-v-755554fe>每次旅遊前，總是浪費大把時間排行程嗎？</h5><h5 class="text-center m-0" data-v-755554fe>快來嘗試專為旅遊設計的行程規劃 App 吧！</h5></div><div class="d-grid gap-2 col-8 col-lg-4 mx-auto mb-5" data-v-755554fe><button class="btn btn-bts btn-lg" type="button" data-bs-toggle="modal" data-bs-target="#PlanningPopup" data-v-755554fe>Behind the scene</button></div><div class="modal fade" id="PlanningPopup" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true" data-v-755554fe><div class="modal-dialog modal-dialog-scrollable" data-v-755554fe><div class="modal-content" data-v-755554fe><div class="modal-header" data-v-755554fe><h4 data-v-755554fe>Kickoff &amp; Survey</h4><button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close" data-v-755554fe></button></div><div class="modal-body" data-v-755554fe><p class="p-0" data-v-755554fe>Google 曾經推出旅遊行程規劃 App - Google Trip，僅上架一年後便停止營運。</p><img src="'+b+'" class="img-fluid px-5" alt="" data-v-755554fe><p class="pt-3" data-v-755554fe>於是我們團隊以年齡、職業、規劃方式、情境等方向設計了一份問卷，透過不同社群管道蒐集到超過兩百份的回饋，希望可以了解各族群規劃旅遊的方式。</p><img src="'+A+'" class="img-fluid" alt="" data-v-755554fe><figcaption class="blockquote-footer text-center pt-3" data-v-755554fe> 不同年齡層之規劃方式比例分布 </figcaption><p data-v-755554fe>其中不規劃旅程的人僅佔不到一成，而 30 歲以下的族群使用手機規劃的比率要大於電腦規劃，<strong data-v-755554fe>仍存在用手機規劃旅遊的需求。</strong></p><p data-v-755554fe>取使用手機規劃行程比例最高的 19-22 歲作為目標族群，探究其使用習慣。</p><img src="'+u+'" class="img-fluid" alt="" data-v-755554fe><figcaption class="blockquote-footer text-center pt-3" data-v-755554fe> 目標族群之不同旅遊規劃方式比例 </figcaption><img src="'+y+'" class="img-fluid" alt="" data-v-755554fe><figcaption class="blockquote-footer text-center pt-3" data-v-755554fe> 目標族群規劃旅遊行程的參考資訊來源 </figcaption><p data-v-755554fe>由此歸納，目標族群規劃旅程的主要習慣為文字紀錄＋Google 搜尋。</p></div><div class="modal-footer" data-v-755554fe><button type="button" class="btn btn-secondary" data-bs-dismiss="modal" data-v-755554fe>Continue</button></div></div></div></div></div></div><div id="item-3" class="mt-5" data-v-755554fe><h2 class="text-center display-5 mb-5 fw-bold" data-v-755554fe>選擇替代方案</h2><img src="'+x+'" class="img-fluid" alt="" data-v-755554fe><div class="row justify-content-center" data-v-755554fe><div class="col-12 col-md-10 mt-4 mb-4" data-v-755554fe><h5 class="text-center m-0" data-v-755554fe>旅遊時總是計劃趕不上變化嗎？</h5><h5 class="text-center m-0" data-v-755554fe>輕鬆選擇鄰近的五星景點吧！</h5></div><div class="d-grid gap-2 col-8 col-lg-4 mx-auto mb-5" data-v-755554fe><button class="btn btn-bts btn-lg" type="button" data-bs-toggle="modal" data-bs-target="#ReschedulePopup" data-v-755554fe>Behind the scene</button></div><div class="modal fade" id="ReschedulePopup" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true" data-v-755554fe><div class="modal-dialog modal-dialog-scrollable" data-v-755554fe><div class="modal-content" data-v-755554fe><div class="modal-header" data-v-755554fe><h4 data-v-755554fe>Interview &amp; Competitive Analysis</h4><button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close" data-v-755554fe></button></div><div class="modal-body" data-v-755554fe><p data-v-755554fe>為深入探索 TA 的目標、需求與痛點，尋找了三名 19-22 歲使用手機規劃旅遊行程，且每月至少出遊一次的旅遊愛好者作訪談對象。</p><p data-v-755554fe>在訪談過程中，請受訪者描述旅遊的過程、遇到的阻礙、使用手機的情境、前往下個行程的行動。</p><img src="'+M+'" class="img-fluid" alt="" data-v-755554fe><figcaption class="blockquote-footer text-center pt-3" data-v-755554fe> Persona of TravelMaker users. </figcaption><p data-v-755554fe>透過訪談，我們發現受訪者開始不使用軟體的契機是在旅遊時計畫有變，臨時修改行程時會直接拿出 Google 搜尋景點，找到後就<strong data-v-755554fe>懶得回去修改行程了。</strong></p><img src="'+h+'" class="img-fluid" alt="" data-v-755554fe><figcaption class="blockquote-footer text-center pt-3" data-v-755554fe> 市面上旅遊軟體的競品分析 </figcaption><p data-v-755554fe>競品分析中可以發現，替代行程功能是現有的旅遊相關產品中，尚未被強調、提供的服務，可能有未被滿足的需求。</p><p data-v-755554fe>為了快速找到理想行程給使用者，我們分析訪談內容，從中建構出行程推薦的邏輯。</p><img src="'+w+'" class="img-fluid" alt="" data-v-755554fe><figcaption class="blockquote-footer text-center pt-3" data-v-755554fe> 分析訪談內容 </figcaption><img src="'+B+'" class="img-fluid" alt="" data-v-755554fe><figcaption class="blockquote-footer text-center pt-3" data-v-755554fe> 行程推薦邏輯 </figcaption></div><div class="modal-footer" data-v-755554fe><button type="button" class="btn btn-secondary" data-bs-dismiss="modal" data-v-755554fe>Continue</button></div></div></div></div></div></div><div id="item-4" class="mt-5 pb-5" data-v-755554fe><img src="'+k+'" class="img-fluid" alt="" data-v-755554fe></div></div></div><div class="col-3 d-none d-lg-block" data-v-755554fe><nav id="sideNav" class="me-5" data-v-755554fe><nav class="nav nav-pills flex-column me-5" data-v-755554fe><a class="nav-link cursorDefault" href="#item-1" data-v-755554fe>TravelMaker</a><a class="nav-link cursorDefault" href="#item-2" data-v-755554fe>Planning</a><a class="nav-link cursorDefault" href="#item-3" data-v-755554fe>Reschedule</a><a class="nav-link cursorDefault" href="#item-4" data-v-755554fe>Target Audience</a><a class="nav-link cursorDefault" href="#item-5" data-v-755554fe>Mockup</a><a class="nav-link cursorDefault" href="#item-6" data-v-755554fe>User Experience</a><a class="nav-link cursorDefault" href="#item-7" data-v-755554fe>Role &amp; Try</a></nav></nav></div><div id="item-5" class="col-12 heroPage mb-5 p-0" data-v-755554fe><img src="'+T+'" class="img-fluid" alt="" data-v-755554fe></div><div class="col-3 d-none d-lg-block" data-v-755554fe></div><div id="item-6" class="col-12 col-lg-6 content mb-5" data-v-755554fe><div class="row g-4 justify-content-center" data-v-755554fe><div class="col-12" data-v-755554fe><h2 class="text-center display-6 fw-bold" data-v-755554fe>我們也重視您的使用體驗</h2></div><div class="col-10 col-md-5 col-lg-6 card-flow" data-v-755554fe><div class="card align-items-center p-2" type="button" data-bs-toggle="modal" data-bs-target="#IAPopup" data-v-755554fe><img src="'+I+'" class="p-3 card-img-top" style="max-width:150px;max-height:150px;" alt="..." data-v-755554fe><div class="card-body" data-v-755554fe><h5 class="card-title text-center" data-v-755554fe>有跡可循的資訊架構</h5><p class="card-text text-center" data-v-755554fe>我們了解遲遲找不到功能、按鈕的煩躁感，我們精心規劃的資訊分層架構讓旅人快速完成操作、繼續旅程。</p></div></div><div class="modal fade" id="IAPopup" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true" data-v-755554fe><div class="modal-dialog modal-dialog-scrollable" data-v-755554fe><div class="modal-content" data-v-755554fe><div class="modal-header" data-v-755554fe><h4 data-v-755554fe>IA &amp; Flow</h4><button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close" data-v-755554fe></button></div><div class="modal-body" data-v-755554fe><p class="p-0" data-v-755554fe>首先整理使用者在每個階段需要的資訊。</p><img src="'+P+'" class="img-fluid" alt="" data-v-755554fe><figcaption class="blockquote-footer text-center pt-3" data-v-755554fe> Information Architecture </figcaption><p class="p-0" data-v-755554fe>再進一步思考版面、資訊配置，並以手繪方式溝通。</p><img src="'+Y+'" class="img-fluid" alt="" data-v-755554fe><figcaption class="blockquote-footer text-center pt-3" data-v-755554fe> Sketch </figcaption><p class="p-0" data-v-755554fe>得出共識後排出 low-fi wireflow 作為雛形。</p><img src="'+C+'" class="img-fluid" alt="" data-v-755554fe><figcaption class="blockquote-footer text-center pt-3" data-v-755554fe> Wireflow </figcaption><p class="p-0" data-v-755554fe>最終將設計發展為可驗證操作的 Prototype 以進行後續應用。</p><img src="'+R+'" class="img-fluid" alt="" data-v-755554fe><figcaption class="blockquote-footer text-center pt-3" data-v-755554fe> Prototype </figcaption></div><div class="modal-footer" data-v-755554fe><button type="button" class="btn btn-secondary" data-bs-dismiss="modal" data-v-755554fe>Continue</button></div></div></div></div></div><div class="col-10 col-md-5 col-lg-6 card-use" data-v-755554fe><div class="card align-items-center p-2" type="button" data-bs-toggle="modal" data-bs-target="#UXPopup" data-v-755554fe><img src="'+E+'" class="p-3 card-img-top" style="max-width:150px;max-height:150px;" alt="..." data-v-755554fe><div class="card-body" data-v-755554fe><h5 class="card-title text-center" data-v-755554fe>經過驗證的操作方式</h5><p class="card-text text-center" data-v-755554fe>透過不斷的迭代驗證，我們找出最直覺的操作方式，減少旅人的阻礙，不必費力學習困難的操作流程。</p></div></div><div class="modal fade" id="UXPopup" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true" data-v-755554fe><div class="modal-dialog modal-dialog-scrollable" data-v-755554fe><div class="modal-content" data-v-755554fe><div class="modal-header" data-v-755554fe><h4 data-v-755554fe>A/B Test</h4><button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close" data-v-755554fe></button></div><div class="modal-body" data-v-755554fe><p class="p-0" data-v-755554fe>團隊在規劃替代行程功能時，遭遇到一個挑戰：<strong data-v-755554fe>「如何讓使用者在旅遊的當下決定是否重新規劃行程？」</strong></p><p data-v-755554fe>為此，我帶領大家做了 A/B testing ，分別將兩組不同的版面交給不同的使用者，並且觀察他們的行動。</p><div class="row" data-v-755554fe><div class="col-6" data-v-755554fe><img src="'+j+'" class="img-fluid" alt="" data-v-755554fe><figcaption class="blockquote-footer text-center pt-3" data-v-755554fe> 延伸按鈕方式 </figcaption></div><div class="col-6" data-v-755554fe><img src="'+S+'" class="img-fluid" alt="" data-v-755554fe><figcaption class="blockquote-footer text-center pt-3" data-v-755554fe> 跳出視窗方式 </figcaption></div></div><p data-v-755554fe>我們注意到，pop up 的組別雖然會更快的點擊「不重新規劃」的按鈕，但是使用者的回饋表示跳出的視窗有一點干擾，並且只會想「快點關掉它」，因此團隊決定使用延伸按鈕的作法。</p></div><div class="modal-footer" data-v-755554fe><button type="button" class="btn btn-secondary" data-bs-dismiss="modal" data-v-755554fe>Continue</button></div></div></div></div></div><div class="col-10 col-md-5 col-lg-6 card-see" data-v-755554fe><div class="card align-items-center p-2" type="button" data-bs-toggle="modal" data-bs-target="#StylePopup" data-v-755554fe><img src="'+Q+'" class="p-3 card-img-top" style="max-width:150px;max-height:150px;" alt="..." data-v-755554fe><div class="card-body" data-v-755554fe><h5 class="card-title text-center" data-v-755554fe>統一簡潔的視覺風格</h5><p class="card-text text-center" data-v-755554fe>能夠在各種旅遊情境下清楚顯示的視覺設計，不再被複雜花俏的風格樣式弄得眼花撩亂。</p></div></div><div class="modal fade" id="StylePopup" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true" data-v-755554fe><div class="modal-dialog modal-dialog-scrollable" data-v-755554fe><div class="modal-content" data-v-755554fe><div class="modal-header" data-v-755554fe><h4 data-v-755554fe>StyleGuide</h4><button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close" data-v-755554fe></button></div><div class="modal-body" data-v-755554fe><p class="p-0" data-v-755554fe>根據目標族群的喜好以及 Apple 的 Human Interface Guideline 建立專屬於 TravelMaker 的 Style Guide.</p><img src="'+H+'" class="img-fluid" alt="" data-v-755554fe><figcaption class="blockquote-footer text-center pt-3" data-v-755554fe> Style Guide </figcaption></div><div class="modal-footer" data-v-755554fe><button type="button" class="btn btn-secondary" data-bs-dismiss="modal" data-v-755554fe>Continue</button></div></div></div></div></div><div class="col-10 col-md-5 col-lg-6 card-test" data-v-755554fe><div class="card align-items-center p-2" type="button" data-bs-toggle="modal" data-bs-target="#NextPopup" data-v-755554fe><img src="'+X+'" class="p-3 card-img-top" style="max-width:150px;max-height:150px;" alt="..." data-v-755554fe><div class="card-body" data-v-755554fe><h5 class="card-title text-center" data-v-755554fe>傾聽意見不斷改進</h5><p class="card-text text-center" data-v-755554fe>旅遊管家持續搜集使用者的意見與回饋並進行修正，同時積極規劃後續發展，帶給旅人更方便的服務。</p></div></div><div class="modal fade" id="NextPopup" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true" data-v-755554fe><div class="modal-dialog modal-dialog-scrollable" data-v-755554fe><div class="modal-content" data-v-755554fe><div class="modal-header" data-v-755554fe><h4 data-v-755554fe>Usability Test &amp; Next Steps</h4><button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close" data-v-755554fe></button></div><div class="modal-body" data-v-755554fe><p class="p-0" data-v-755554fe>後續規劃進行易用性測試以持續優化使用體驗。</p><img src="'+O+'" class="img-fluid" alt="" data-v-755554fe><figcaption class="blockquote-footer text-center pt-3" data-v-755554fe> Usability Test Plan </figcaption><p class="p-0" data-v-755554fe>持續優化既有產品，並納入更多功能以提供更全面的旅遊助理服務。</p><img src="'+q+'" class="img-fluid" alt="" data-v-755554fe><figcaption class="blockquote-footer text-center pt-3" data-v-755554fe> 可發展功能與優化項目 </figcaption></div><div class="modal-footer" data-v-755554fe><button type="button" class="btn btn-secondary" data-bs-dismiss="modal" data-v-755554fe>Continue</button></div></div></div></div></div></div></div><div class="col-3 d-none d-lg-block" data-v-755554fe></div><div id="item-7" class="col-12" data-v-755554fe><div class="row justify-content-center" data-v-755554fe><div class="col-12 col-md-5 col-lg-3 align-content-center pb-5" data-v-755554fe><h4 class="text-center fw-bold text-white pt-5" data-v-755554fe>Role</h4><p class="text-center text-white mb-0" data-v-755554fe>Project Manage</p><p class="text-center text-white mb-0" data-v-755554fe>Research</p><p class="text-center text-white mb-0" data-v-755554fe>Interaction Design</p><p class="text-center text-white mb-0" data-v-755554fe>Information Architecture</p><h4 class="text-center fw-bold text-white pt-3" data-v-755554fe>Duration</h4><p class="text-center text-white mb-0" data-v-755554fe>Fall 2021</p></div><div class="col-6 col-md-5 col-lg-3 mt-md-5 pt-2 pb-5 d-grid gap-1" data-v-755554fe><div class="text-center" data-v-755554fe><img src="'+D+'" class="img-fluid" alt="" data-v-755554fe></div><a href="https://www.figma.com/proto/prrQCaOrDpzMd3DmCon6VX/Travel-Maker?kind=&amp;node-id=364%3A3631&amp;page-id=364%3A366&amp;scaling=scale-down&amp;show-proto-sidebar=1&amp;starting-point-node-id=364%3A3631" class="btn btn-cta mt-4 mb-2 fs-5 fw-bold" target="_blank" type="button" data-v-755554fe>Try TravelMaker</a></div></div></div></div>',1),Z=[U],J={__name:"TravelMaker",setup(N){let t,s,e;c(()=>{e=document.querySelector("#sideNav"),t=e.getElementsByClassName("nav-link"),s=document.querySelector(".banner"),window.addEventListener("scroll",d)});function d(){if(s.getBoundingClientRect().bottom<=150){e.style.opacity=1;for(let a=0;a<t.length;a++)t[a].classList.remove("cursorDefault")}else{e.style.opacity=0;for(let a=0;a<t.length;a++)t[a].classList.add("cursorDefault")}}return r((o,a,l)=>{window.removeEventListener("scroll",d),console.log("remove"),l()}),(o,a)=>(n(),v("section",G,Z))}},W=i(J,[["__scopeId","data-v-755554fe"]]);export{W as default};
